document.addEventListener("DOMContentLoaded", function () {
  const marqueeContent = document.querySelector(".marquee-content");
  const marqueeContainer = document.querySelector(".marquee-container");

  const calculateAnimationDuration = () => {
    // Get the width of a single text block
    const textWidth = marqueeContent.querySelector(".text").offsetWidth;
    // Total width is twice the text width (since there are two .text spans)
    const totalWidth = textWidth * 2;
    const containerWidth = marqueeContainer.offsetWidth;
    // The animation should move the distance of one block, hence totalWidth - containerWidth
    const distanceToMove = totalWidth - containerWidth;
    const speed = 200; // Adjust this value for speed, higher is slower
    return distanceToMove / speed;
  };

  const setAnimationDuration = () => {
    const duration = calculateAnimationDuration();
    marqueeContent.style.animationDuration = `${duration}s`;
  };

  setAnimationDuration();
  window.addEventListener("resize", setAnimationDuration); // Recalculate on window resize
});
